import React,{Component} from 'react';
import './style.css'

class ControlledComponent extends Component{

   constructor(props){
     super(props);
     this.state = {
         username: 'java'
     }
     this.submit = this.submit.bind(this);
     this.handleUserChange = this.handleUserChange.bind(this);
     

   }
   
   submit(event){
    // alert('You have clicked on submit button')
     event.preventDefault();
     console.log(this.username.current.value)
   }

   handleUserChange(event){

       console.log(event.target.value)

   }
  

    render(){
      return(
        <div className='UnControlled'>
          <form onSubmit={this.submit}>
             <label>Username:</label>
             <input ref={this.username} type="text" name="username" value={this.state.username} onChange={this.handleUserChange}></input>
             <label>Password:</label>
             <input ref={this.pass} type="password" name="password"></input>
             <button className="button" style={{backgroundColor:"green"}} value="submit">
                 Button
             </button>  
            </form>   
        </div>
      )
    }


  

}



export default ControlledComponent;
