import './style.css'

class ControlledComponent extends Component{

   constructor(props){
     super(props);
     this.state = {
         username: 'java',
         password: ''
     }
    // this.handlePasswordChange = this.handlePasswordChange.bind(this);
    // this.handleUserChange = this.handleUserChange.bind(this);

     this.handleUserChange = this.handleUserChange.bind(this);
     

   }
   
  

   handleChange(event){

       console.log(event.target.value)

       this.setState({
           username : event.target.value
       })

   }

  
   handleLogin
  

    render(){
      return(
        <div className='UnControlled'>
          <form onSubmit={this.handleLogin}>
             <label>Username:</label>
             <input ref={this.username} type="text" name="username" value={this.state.username} onChange={this.handleChange}></input>
             <label>Password:</label>
             <input ref={this.pass} type="password" name="password" value={this.state.password} onChange={this.handleChange}></input>
             <button className="button" style={{backgroundColor:"green"}} value="submit">
                 Button
             </button>  
            </form>   
        </div>
      )
    }


  

}



export default ControlledComponent;
